        </div>
      </div>

      {/* Estilos CSS para mostrar/ocultar seg√∫n tama√±o de pantalla */}
      <style jsx>{`
        .invitation-mobile {
          display: none;
        }
        .desktop-message {
          display: flex;
        }
        @media (max-width: 768px) {
          .invitation-mobile {
            display: block;
          }
          .desktop-message {
            display: none;
          }
        }
      `}</style>
    </>
  );
};

export default GraduationInvitation;
