import React, { useState } from "react";
import bk from "../bk.jpeg"; // ajusta según tu estructura

const GraduationInvitation = () => {
  const [open, setOpen] = useState(false);
  const [fitMode, setFitMode] = useState("cover"); // "cover" | "contain"

  return (
    <div
      className="min-h-screen flex flex-col items-center justify-center p-6 gap-6"
      style={{ background: "#f0f0f5", fontFamily: `"Century Gothic", "Apple Gothic", Arial, sans-serif` }}
    >
      {/* Switch para probar fit mode */}
      <div className="flex gap-4 items-center">
        <span style={{ fontSize: "0.9rem" }}>Ver imagen como:</span>
        <button
          onClick={() => setFitMode("cover")}
          className={`px-4 py-1 rounded-full border ${
            fitMode === "cover" ? "bg-black text-white" : "bg-white text-black"
          }`}
          style={{ fontSize: "0.8rem" }}
        >
          Cover
        </button>
        <button
          onClick={() => setFitMode("contain")}
          className={`px-4 py-1 rounded-full border ${
            fitMode === "contain" ? "bg-black text-white" : "bg-white text-black"
          }`}
          style={{ fontSize: "0.8rem" }}
        >
          Contain
        </button>
      </div>

      <div className="relative max-w-md w-full rounded-3xl shadow-xl border border-gray-200 overflow-hidden">
        {/* Área de imagen con proporción fija para que se vea parte sin romper */}
        <div
          className="relative w-full"
          style={{
            aspectRatio: "4 / 3", // puedes cambiar a 3/2, 16/9, etc según cuánto quieras mostrar
          }}
        >
          <img
            src={bk}
            alt="fondo"
            className="absolute inset-0 w-full h-full"
            style={{
              objectFit: fitMode,
              filter: "brightness(1)",
              transition: "object-fit .2s ease",
            }}
          />
          {/* overlay ligero para legibilidad */}
          <div
            className="absolute inset-0"
            style={{
              backgroundColor: "rgba(255,255,255,0.6)",
              backdropFilter: "blur(1px)",
            }}
          />
        </div>

        {/* Contenido encima */}
        <div className="p-8 text-center relative">
          <p className="text-sm uppercase tracking-widest mb-2" style={{ letterSpacing: "2px" }}>
            Acompáñame en esta celebración
          </p>

          <h1 className="text-4xl mb-1" style={{ lineHeight: 1.1, fontWeight: 700 }}>
            Obtención de Grado <br /> <span style={{ fontSize: "0.8em" }}>(o no) 😅</span>
          </h1>

          <h2 className="text-2xl mt-2 mb-4" style={{ fontWeight: 700, letterSpacing: "0.5px" }}>
            Doctorado <br />
            Ciencias en Alimentos
          </h2>

          <div className="text-center mb-6" style={{ fontSize: "1rem", lineHeight: 1.3, color: "#2d2d2d" }}>
            <p className="mb-1">
              <strong style={{ fontWeight: "600" }}>Fecha:</strong> 19 de Agosto de 2025
            </p>
            <p className="mb-1">
              <strong style={{ fontWeight: "600" }}>Hora:</strong> 1:00 pm
            </p>
            <p className="mb-1">
              <strong style={{ fontWeight: "600" }}>Lugar:</strong> Higgs #36 Col. El paraíso
            </p>
          </div>

          <p className="mb-8" style={{ fontSize: "1rem", lineHeight: 1.2 }}>
            Tu presencia es importante para mí <span>😊</span>
          </p>

          <div className="flex flex-col sm:flex-row sm:justify-center gap-3">
            <button
              onClick={() => setOpen(true)}
              className="px-6 py-2 rounded-full bg-black text-white text-sm font-medium hover:opacity-90 transition"
              style={{ fontFamily: `"Century Gothic", "Apple Gothic", Arial, sans-serif` }}
            >
              📍 Ver ubicación
            </button>
            <a
              href="https://wa.me/5213113001707?text=%C2%A1S%C3%AD%20asistir%C3%A9%20a%20la%20graduaci%C3%B3n%20de%20la%20Dra.%20Karla%20Ayon!"
              target="_blank"
              rel="noopener noreferrer"
              className="px-6 py-2 rounded-full bg-green-600 text-white text-sm font-medium hover:opacity-90 transition"
              style={{ fontFamily: `"Century Gothic", "Apple Gothic", Arial, sans-serif` }}
            >
              ✅ Confirmar asistencia
            </a>
          </div>
        </div>
      </div>

      {/* Modal de ubicación */}
      {open && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
          <div
            className="bg-white rounded-2xl shadow-2xl p-6 max-w-sm w-full text-center relative"
            style={{ fontFamily: `"Century Gothic", "Apple Gothic", Arial, sans-serif` }}
          >
            <h2 className="text-xl font-semibold mb-3">Ubicación del Evento</h2>
            <p className="text-gray-700 mb-4">Higgs #36 Col. El paraíso</p>
            <iframe
              className="w-full h-48 rounded-lg mb-4"
              src="https://www.google.com/maps?q=Higgs%20%2336%20Col.%20El%20para%C3%ADso&output=embed"
              allowFullScreen
              loading="lazy"
              title="mapa"
            />
            <button
              onClick={() => setOpen(false)}
              className="mt-2 px-5 py-2 bg-black text-white rounded-full hover:opacity-90 transition"
            >
              Cerrar
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default GraduationInvitation;
